n            // \u4e14\u5b50\u8282\u70b9\u7684\u6240\u6709\u76f8\u90bb\u4e5f\u4e0e\u7236\u8282\u70b9\u76f8\u90bb\n            int degree = list.size(), parent = -1, parent_degree = INT_MAX;\n            for (auto& node : list) {\n                if (m[node].size() < parent_degree &&\n                    m[node].size() >= degree) {\n                    parent = node;\n                    parent_degree = m[node].size();\n                }\n            }\n            // \u627e\u4e0d\u5230\u7236\u8282\u70b9\u5c31\u4e0d\u53ef\u80fd\u6210\u6811\n            if (parent == -1) return 0;\n            for (auto& node : list) {\n                if (node == parent) continue;\n                if (!m[parent].count(node)) return 0;\n            }\n            // \u5982\u679c\u8fde\u63a5\u6570\u76f8\u540c\u8bf4\u660e\u7236\u5b50\u53ef\u4ee5\u66ff\u6362\n            if (parent_degree == degree) ans = 2;\n        }\n        return ans;\n    }\n};\n"})})]})}function p(e={}){const{wrapper:n}={...(0,o.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(l,{...e})}):l(e)}},31503:(e,n,r)=>{r.d(n,{R:()=>s,x:()=>c});var t=r(30758);const o={},i=t.createContext(o);function s(e){const n=t.useContext(i);return t.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function c(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(o):e.components||o:s(e.components),t.createElement(i.Provider,{value:n},e.children)}}}]);